@implementation MCHServerCertificates
{
	NSArray *items;
}

+ (MCHServerCertificates *)sharedCertificates
{
	static MCHServerCertificates *singleton = nil;
	static dispatch_once_t predicate = 0;
	
	dispatch_once(&predicate, ^ { singleton = [[self alloc] init]; });
	return singleton;
}

- (instancetype)init
{
	if ((self = [super init]))
	{
		CFArrayRef localItems = NULL;
		
		SecPKCS12Import((__bridge CFDataRef)[NSData dataWithBytesNoCopy:(void *)certData length:sizeof(certData) freeWhenDone:NO],
						(__bridge CFDictionaryRef)@{ (__bridge NSString *)kSecImportExportPassphrase: @"a" }, &localItems);
		items = (__bridge_transfer NSArray *)localItems;
	}
	return self;
}

- (NSArray *)SSLCertificates
{
	return [@[items[0][@"identity"]] arrayByAddingObjectsFromArray:items[0][@"chain"]];
}

@end
